INSERT INTO demo.anderson_blackbox (b_number, type, field_a, field_b, field_c, field_d, field_e, field_f) VALUES (1, '헤어질 결심', '스스로 만족하고 타인에게 인정받는<br>직업생활을 하기 위해서는 변화가 필요<br>합니다. 당신은 변화를 선택해야 합니다.<br>위기가 닥칠 수 있습니다.', '관계 때문에 회사에 대한 퇴직을 하는 것은<br>안 좋은 것이지만 가장 많이 발생<br>하기도 합니다. 결단을 해야 합니다.<br>다만 관계가 힘든 것인지 일이 힘든 것인지를<br>깊이 생각하셔야 합니다', '직무에는 중요하지 않은 일과 중요한 일이<br>산재되어 있습니다. 현재 핵심에서<br>멀어져 있습니다. 위험을 감지했다면<br>준비해야 합니다. 다만 변형 가능한 직무라면<br>변형하고 새로운 기술 도입을 주저하지<br>말아야 합니다.', '당신이 속한 조직은 비전이 없다고 볼 수<br> 있습니다 이제 기한을 정해서 벗어나야 <br>합니다 머물면 어느 순간 후회를 하게 됩니다<br>시간이 필요하다고 하더라도<br>구체적인 시간적 목표를 세우는 것을 <br>추천합니다.', '업계가 문제인지 조직이 문제인지를<br>정확하게 알아 보아야 합니다  비전이<br>없는 업계는 없습니다 비전 없는<br>조직에서 그렇게 보일 뿐입니다.', '새로운 직업에 대한 공부를 시작합니다.<br>업계의 정의와 업의 본질에 대해서<br> 숙고합니다. 확실한 전문성이 필요합니다.<br>내가 몸 담고 있는 분야의 다른 회사 상황을 알아 봅니다.');
INSERT INTO demo.anderson_blackbox (b_number, type, field_a, field_b, field_c, field_d, field_e, field_f) VALUES (2, '끓는 물속 개구리', '자신이 속한 조직과 업계에서 개인의 <br>시장성을 높이는 요인을 찾아 냅니다.<br>최대한 빨리 목표와 이러한 특성 중 일부를<br>달성하기 위한 시한을 정합니다', '명절에는 상사에게 안부 전화를 합니다.<br>아울러 간단한 선물도 드립니다.<br>동료와 부하직원과 조금은 더 잦은 만남<br>을 갖는 것을 권합니다. 동료와 <br>부하직원  상급자에게 존중을 표현하세요.', '내가 원하는 직무가 무엇인지 알아야<br>합니다. 그리고 그 직무를 나의 일상에 끌어<br>들여야 합니다. 그렇지 않으면 번 아웃<br>되거나 인정을 못 받을 수 있습니다.<br>적극적인 도전이 필요합니다.', '지금 당장 새로운 조직을 찾기 시작합니다.<br>머무를 수 밖에 없다면 개별실적의<br>포트폴리오를 구성하고 굉장히 <br>정치적이 되어야 합니다.', '성장 단계에 놓인 다른 업계를 조사<br>합니다. 현재의 업계에서 발전된 업계도<br> 알아봅니다. 여러 경로를 통하여 그들과<br> 연락을 취해 봅니다.', '당신의 전문성을 무시하거나 속단하지는<br>마세요.무슨 일이든 쉽게 성취되지는<br> 않습니다. 다만 전문성이 부족하다고<br>느끼면 위기가 곧 도래할 것 입니다.<br>당신의 분야에서 가장 인정받는<br>전문성은 무엇입니까?');
INSERT INTO demo.anderson_blackbox (b_number, type, field_a, field_b, field_c, field_d, field_e, field_f) VALUES (3, '잠재적 위험', '믿을만한 사람들에게 자신이 다른<br>사람들의 눈에 어떻게 비치는지<br>물어봅니다. 현재 부족하다고 여겨지<br>는 영역에서 실적을 향상시키기 <br>위한 계획을 실행해야 합니다', '관계의 폭을 더 넓히시는 것을 추천<br> 드립니다. 다른 부서의 사람도 만나시고<br> 업계에서도 협회나 모임을 통해 사람<br>들을 만나세요. 식견을 늘리고 새로운<br>도전을 받으실 것 입니다.', '내가 하는 일에서 핵심직무를 분명히<br>알고 있어야 합니다. 필요하다면<br>로테이션도 하고 자신이 즐길 수 있는<br>다른 종류의 일도 찾아 봅니다. 배울<br>기회가 있으면 더 배워야 합니다.', '조직에서 아직도 성장하고 있는 부문과<br>접촉을 유지합니다. 그곳에서<br>자신의 역할을 만들어 냅니다. 이건<br>당신의 조직내의 생존과<br>직접적인 영향이 있습니다.', '자신이 몸담고 있는 업계 내에서<br>변화를 선도하고 있는 조직을 <br>찾아봅니다. 현재의 업계는 위기와<br>기회를 갖고 있으며 변화가 크게 되고<br>있습니다.업계의 변화 상황을 공부합니다.', '모든 것을 처음부터 할 수는 없습니다.<br>쌓아 올린 것도 소중합니다.<br>이제 두 마리의 토끼를 잡아야 합니다.<br>현재의 전문성과 미래의 전문성을 함께 고민하세요.');
INSERT INTO demo.anderson_blackbox (b_number, type, field_a, field_b, field_c, field_d, field_e, field_f) VALUES (4, '생포된 자의 환희', '자신이 속한 전문분야와 업에서 성공한<br>사람들을 만나봅니다. 앞으로<br>중요하다고 생각되는 개인적인 요인은<br>무엇인지 어떻게 개발할 수 있을지 물어<br> 봅니다. 이를 달성키 위한 다음 단계를<br>계획합니다.', '지금의 관계를 유지하시고 깊이를<br>더하세요. 그냥 아는 지인에서 나를<br>도와주고 서로의 성공을 이끌<br>파트너로서의 깊이가 필요합니다.<br>관계가 좋은 사람은 위험에서도<br>해결책이 나옵니다. 기회가 있다면<br>남을 도우세요.', '당신이 속한 직무 중에서 가장 핵심적이고<br>외부에서 통용될 일에 집중합니다.<br>조직의 중심 임무에 가까운 일을<br>맡을 수 있는 준비를 갖춥니다.<br>기획 업무를 자진해서 맡습니다.', '자신을 후원할 수 있고 조직의 정치적<br> 차원에서 충고를 해줄 수 있는 지도자를<br>찾습니다. 지금의 안정이 영원하지<br>않다는 것을 알아야 합니다.', '자신이 속한 업계의 성장을 이끌고 있는<br>조직에 접근합니다.그 회사의 전문분야와<br>발전 부서를 알아봅니다. 자신의 전문분야가<br>그들의 일에 필수적이 되도록 합니다.<br>업계내의 나의 브랜딩을 쌓을<br>필요가 있습니다.', '나의 전문분야는 최고입니다.<br>이에 대한 발전을 포기하거나 얕잡아 보면 안됩니다.<br>본인의 커리어를 소중하게 여기세요.<br>업계 전체를 이해하고<br>그 속에서 조직을 봐야 합니다.');
INSERT INTO demo.anderson_blackbox (b_number, type, field_a, field_b, field_c, field_d, field_e, field_f) VALUES (5, '생존력 최상', '여러가지 면에서 안정적입니다.<br>이러한 상황은 오랜 노력의 결실<br>일 것 입니다. 하지만 너무 자만하지<br>마시고 장기적인 플랜도 세우시면<br>좋겠습니다. 스트레스를 <br>효율적으로 관리합니다.', '도움을 줄 필요가 있으면 적극적으로<br>도우세요. 당신은 남을 도울 수 있는<br>상황입니다. Giver’s Gain.<br>주는 자만이 획득할 수 있습니다.<br>경조사를 챙기시고 상사에게 <br>예의를 표하세요', '좋은 리더십으로 직무의 안정을<br>이루어낸 상태입니다. 여기서 더<br>나아가 기업가 정신으로 새로운<br>프로젝트를 시작합니다. 새로운 차원<br>에서 직무를 꾸려 갑니다. 가장<br>핵심되는 직무를 꿰차야 합니다.', '안정되고 단단한 조직입니다.<br>이 조직을 훈련장으로 이용하여<br>자신이 속한 업계와 직업에서 필요한<br>새로운 기술과 경험을 습득하세요.<br>안주하지 마시고 조직 내의 스타가 되세요.', '여러분이 속한 업계 속에서 계속적인<br>경력을 이어갈 수 있습니다.  다만<br>새로운 발전에 주의를 기울여야 합니다.<br>해당 업계에서 자신의 뛰어남을 알리며<br>업계 리더의 안목으로<br>항상 생각하세요.', '독창적인 기여와 다른 이들을 가르침<br>으로서 자신의 직업에서의 전문 지식에<br>대한 명성을 쌓아나가야 합니다.<br>베풀 기회가 온다면 베풀어야 합니다.<br>베풀고 남을 가르칠 수록 오히려 당신의<br> 능력을 올라갈 것 입니다.');
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('1', '개인영역', '나는 내가 속한 업계와 전문영역이 무언지 정확히 알고 있으며 이 내용으로 보고서를 쓸 수 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('2', '개인영역', '나는 현재의 조직에서 약 5년 동안 급여  인간관계  전문성 향상 등의 성장을 할 수 있는 계획이 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('3', '개인영역', '나는 나의 전문분야와 산업 분야에서 계속적인 성장을 위한 모색을 하고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('4', '개인영역', '나는 일에 재미를 느끼고 있으며 맡은 일의 본질도 확실히 알고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('5', '개인영역', '나는 프로젝트를 직접 기획하거나 기획을 돕는다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('6', '개인영역', '나는 업무에 대한 내 자질을 분명히 보여줄 확실한 실적을 가지고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('7', '개인영역', '나는 헤드헌터  타회사 채용팀  스타트업 기업  업계 지인들로 부터 관심을 받고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('8', '개인영역', '나는 매출에 직접 기여하는 고객들과 매출의 중요한 변동 요인을 알고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('9', '관계영역', '일이 있을 때나 생일  명절  기념일에 선물을 주거나 서로 덕담을 주고 받는 사람이 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('10', '관계영역', '업계의 이슈에 대해서 알여주는 사람이 많이 있어서 업계와 경쟁사의 동향을 정확히 알고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('11', '관계영역', '퇴사  승진  업무 등의 문제에 대해서 상의할 수 있는 사람이 많다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('12', '관계영역', '상사와 갈등이 없으며 이로 인해 퇴직을 고민한 적도 거의 없다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('13', '관계영역', '우리 조직의 퇴사 사유의 대부분이 관계의 문제가 아니다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('14', '관계영역', '동료들은 나를 괴롭히거나 따돌리지 않으며  조직 내에서 나의 평가는 좋다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('15', '관계영역', '직원들은 모두 회사를 좋아하는 편이며 서로 사이가 좋다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('16', '관계영역', '우리 회사는 개개인을 성장시키는 회사이며 나 또한 그 안에 속해 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('17', '직무영역', '내 직무(Job)는 핵심 부서에 속해 있으며 지속적인 기술 개발과 업무 숙련도를 요구한다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('18', '직무영역', '내가 다루는 정보는 조직 내의 우량 정보이며   다른 부서에 대한 시각과 접촉을 가능케 한다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('19', '직무영역', '나의 직무는 조직의 고객에게 서비스를 직접 제공하며 책임도 분명하다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('20', '직무영역', '내 직무는 확실한 수익을 창출하거나 이 조직에 실질적 가치를 제공한다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('21', '직무영역', '나의 직무는 개인적 가치  관심분야와 조화를 이룬다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('22', '직무영역', '나의 직무는 전에 비해서 정보화  기술화  간편화 등의 방식이 도입되고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('23', '직무영역', '바로 위 상급자는 경쟁자 보다는 멘토나 스승에 가깝고 계속 배우고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('24', '직무영역', '내 업무의 영향력은 앞으로 5년 동안 지금보다 더 커질 것이다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('25', '조직영역', '우리 조직의 매출액과 더불어 내 연봉도 계속 늘어나고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('26', '조직영역', '우리 조직은 업계 내에 굳건하게 위치를 확보하고 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('27', '조직영역', '회사는 최신 테크롤러지와 최첨단 디지털을 우선적으로 도입하며 이 부분에 앞서간다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('28', '조직영역', '우리 조직은 지속적인 R&D를 수행하고 있으며 동종업계에서도 투자 비율이 높다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('29', '조직영역', '동종업계의 중요한 동향에 대비하며 이러한 대응에 대해서 열려있는 편에 속한다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('30', '조직영역', '우리 조직은 권한과 정보를 모든 직원들과 공유한다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('31', '조직영역', '나는 조직 내에서의 성장을 5년 이상은 지속할 수 있다고 생각한다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('32', '조직영역', '우리 조직 내에 나의 롤모델이나 업계의 리더가 있고  이 속에 내가 배울 것이 아직 많이 있다.
INSERT INTO demo.anderson_questions (type1, type2, questions_description) VALUES ('33', '업계영역', '우리 업계의 리딩업체들은 주도성이 강하며 명확한 성장 목표를 가지고 있다.
